```typescript
'use client';

import { useState } from 'react';
import DesktopIcon from './DesktopIcon';
import Window from './Window';
import { desktopIcons } from '@/data/win95-icons';

export default function Desktop() {
  const [selectedIcon, setSelectedIcon] = useState<string | null>(null);
  const [openWindows, setOpenWindows] = useState<string[]>([]);

  const handleIconClick = (id: string) => {
    setSelectedIcon(id);
  };

  const handleIconDoubleClick = (icon: DesktopIconType) => {
    if (icon.action === 'navigate' && icon.route) {
      router.push(icon.route);
    } else if (icon.action === 'open_window') {
      setOpenWindows([...openWindows, icon.id]);
    }
  };

  const handleDesktopClick = () => {
    setSelectedIcon(null);
  };

  return (
    <div 
      className="desktop" 
      onClick={handleDesktopClick}
      style={{ height: 'calc(100vh - 28px)' }}
    >
      {desktopIcons.map(icon => (
        <DesktopIcon
          key={icon.id}
          icon={icon}
          selected={selectedIcon === icon.id}
          onClick={() => handleIconClick(icon.id)}
          onDoubleClick={() => handleIconDoubleClick(icon)}
        />
      ))}

      {openWindows.map(windowId => (
        <Window
          key={windowId}
          windowId={windowId}
          onClose={() => setOpenWindows(openWindows.filter(w => w !== windowId))}
        />
      ))}
    </div>
  );
}
```

